tinymce.PluginManager.add('imageupload', function(editor, url) {
    editor.addButton('imageupload', {
        tooltip: 'Upload/edit Image',
        text: '',
        icon: 'image',
        onclick: function() {
            var find = tinymce.get("textus").windowManager.open({
                title: 'Select Media',
                url: 'tinymce_image-include',
                buttons: [{
                    text: 'Cancel',
                    onclick: 'close'
                }],
                width: 800,
                height: 500
            });
        }
    });
});